edit.action:
//----[ signature forum select - imkingdavid ]----\\
	$sigforums = explode(',', $row['signature_forum_select']);
	$s_show_sig = (in_array($row['forum_id'], $sigforums)) ? false : true;
//----[ end signature forum select - imkingdavid ]----\\

//----[ signature forum select - imkingdavid ]----\\
		'S_SHOW_SIGNATURE'	=> $s_show_sig,
//----[ end signature forum select - imkingdavid ]----\\

	);

	return;
}
/**
* Generate Multi-Select Jumpbox - imkingdavid (c) 2010
*/
function make_multi_jumpbox($action, $forum_ids = array(), $select_all = false, $acl_list = false, $force_display = false)
{
	global $config, $auth, $template, $user, $db;

	$sql = 'SELECT forum_id, forum_name, parent_id, forum_type, left_id, right_id
		FROM ' . FORUMS_TABLE . '
		ORDER BY left_id ASC';
	$result = $db->sql_query($sql, 600);

	$right = $padding = 0;
	$padding_store = array('0' => 0);
	$display_jumpbox = false;
	$iteration = 0;

	// Sometimes it could happen that forums will be displayed here not be displayed within the index page
	// This is the result of forums not displayed at index, having list permissions and a parent of a forum with no permissions.
	// If this happens, the padding could be "broken"

	while ($row = $db->sql_fetchrow($result))
	{
		if ($row['left_id'] < $right)
		{
			$padding++;
			$padding_store[$row['parent_id']] = $padding;
		}
		else if ($row['left_id'] > $right + 1)
		{
			// Ok, if the $padding_store for this parent is empty there is something wrong. For now we will skip over it.
			// @todo digging deep to find out "how" this can happen.
			$padding = (isset($padding_store[$row['parent_id']])) ? $padding_store[$row['parent_id']] : $padding;
		}

		$right = $row['right_id'];

		if ($row['forum_type'] == FORUM_CAT && ($row['left_id'] + 1 == $row['right_id']))
		{
			// Non-postable forum with no subforums, don't display
			continue;
		}

		if (!$auth->acl_get('f_list', $row['forum_id']))
		{
			// if the user does not have permissions to list this forum skip
			continue;
		}

		if ($acl_list && !$auth->acl_gets($acl_list, $row['forum_id']))
		{
			continue;
		}

		if (!$display_jumpbox)
		{
			$template->assign_block_vars('jumpbox_forums', array(
				'FORUM_ID'		=> ($select_all) ? 0 : -1,
				'FORUM_NAME'	=> ($select_all) ? $user->lang['ALL_FORUMS'] : $user->lang['SELECT_FORUM'],
				'S_FORUM_COUNT'	=> $iteration)
			);

			$iteration++;
			$display_jumpbox = true;
		}
		if(is_array($forum_ids))
		{
			$selected = (in_array($row['forum_id'], $forum_ids) && ($row['forum_type'] != FORUM_CAT)) ? ' selected="selected"' : '';
		}
		else
		{
			$selected = (($row['forum_id'] == $forum_ids) && ($row['forum_type'] != FORUM_CAT)) ? ' selected="selected"' : '';
		}
		$template->assign_block_vars('multi_jumpbox_forums', array(
			'FORUM_ID'		=> $row['forum_id'],
			'FORUM_NAME'	=> $row['forum_name'],
			'DISABLED'		=> ($row['forum_type'] == FORUM_CAT) ? ' disabled="disabled"' : '',
			'SELECTED'		=> $selected,
			'S_FORUM_COUNT'	=> $iteration,
			'S_IS_CAT'		=> ($row['forum_type'] == FORUM_CAT) ? true : false,
			'S_IS_LINK'		=> ($row['forum_type'] == FORUM_LINK) ? true : false,
			'S_IS_POST'		=> ($row['forum_type'] == FORUM_POST) ? true : false)
		);

		for ($i = 0; $i < $padding; $i++)
		{
			$template->assign_block_vars('multi_jumpbox_forums.level', array());
		}
		$iteration++;
	}
	$db->sql_freeresult($result);
	unset($padding_store);

	$template->assign_vars(array(
		'S_DISPLAY_MULTI_JUMPBOX'	=> $display_jumpbox,
		'S_MULTI_JUMPBOX_ACTION'	=> $action)

//----[ signature forum select - imkingdavid ]----\\
//----[ this grabs all of the forums the user can see and puts them into a select list ]----\\
				$sql = 'SELECT signature_forum_select
					FROM ' . USERS_TABLE . ' WHERE user_id = ' . $user->data['user_id'];
				$result = $db->sql_query($sql);
				$sigs = $db->sql_fetchrow($result);
				$db->sql_freeresult($result);
				$sigs = explode(',', $sigs['signature_forum_select']);
				make_multi_jumpbox("{$phpbb_root_path}ucp.php?mode=signature", $sigs);
//----[ end signature forum select - imkingdavid ]----\\

//----[ signature forum select - imkingdavid ]----\\
					$forum_ids = request_var('forum_ids', array(0));
					$forum_ids = implode(',', $forum_ids);
					$forum_ids = ($forum_ids != '' && substr($forum_ids, -1) != ',') ? $forum_ids . ',' : $forum_ids;
//----[ end signature forum select - imkingdavid ]----\\

//----[ signature forum select ]----\\
								'signature_forum_select'		=> $forum_ids,
//----[ end signature forum select ]----\\

//----[ signature forum select - imkingdavid ]----\\
	'DISPLAY_SIGNATURE_IN'		=> 'Do not display this signature in',
	'DISPLAY_SIGNATURE_IN_EXPLAIN'	=> 'Use Ctrl+Click to<br />select and deselect forums.<br />',
//----[ end signature forum select - imkingdavid]----\\
